import {getConnection} from "./../database/database"
import multer from 'multer';
import fs from 'fs'; // Si aún no está importado

// Configuración de Multer para almacenar archivos en una ubicación temporal
const upload = multer({ dest: 'uploads/' });


const getVideojuegos= async (req,res) =>{

    try{
        const connection = await getConnection();
        const result = await connection.query("SELECT id,nombre,categoria,multijugador,precio,desarrolladora,url_img FROM videojuegos")

        res.json(result)

    }catch(error){
        res.status(500)
        res.send(error.message)
    }
    
};

